import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [search, setSearch] = useState("");
  const [articles, setArticles] = useState([]);
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    // Exemplo: Carregar dados do Código Penal (Planalto.gov.br)
    // Aqui, você pode substituir por um fetch real da fonte oficial
    fetch("/codigo-penal.json")
      .then((res) => res.json())
      .then((data) => {
        setArticles(data.articles || []);
        setFiltered(data.articles || []);
      });
  }, []);

  useEffect(() => {
    if (!search) {
      setFiltered(articles);
    } else {
      setFiltered(
        articles.filter((a) =>
          a.text.toLowerCase().includes(search.toLowerCase()) ||
          a.number.toString().includes(search)
        )
      );
    }
  }, [search, articles]);

  return (
    <div className="app-container">
      <div className="header">
        <div className="logo">JP</div>
        <div className="title">Código Penal Brasileiro</div>
      </div>
      <div className="search-bar">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          placeholder="Buscar artigo, número ou termo..."
        />
      </div>
      <div className="articles-list">
        {filtered.length === 0 && <p>Nenhum artigo encontrado.</p>}
        {filtered.map((a) => (
          <div key={a.number} className="article-card">
            <div className="article-number">Art. {a.number}</div>
            <div className="article-text">{a.text}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
