import React, { useState, useEffect } from "react";

function App() {
  const [search, setSearch] = useState("");
  const [articles, setArticles] = useState([]);
  const [filtered, setFiltered] = useState([]);

  useEffect(() => {
    // Exemplo: Carregar dados do Código Penal (Planalto.gov.br)
    // Aqui, você pode substituir por um fetch real da fonte oficial
    fetch("/codigo-penal.json")
      .then((res) => res.json())
      .then((data) => {
        setArticles(data.articles || []);
        setFiltered(data.articles || []);
      });
  }, []);

  useEffect(() => {
    if (!search) {
      setFiltered(articles);
    } else {
      setFiltered(
        articles.filter((a) =>
          a.text.toLowerCase().includes(search.toLowerCase()) ||
          a.number.toString().includes(search)
        )
      );
    }
  }, [search, articles]);

  return (
    <div style={{ maxWidth: 700, margin: "40px auto", padding: 20 }}>
      <h2>Código Penal Brasileiro</h2>
      <input
        type="text"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        placeholder="Buscar artigo, número ou termo..."
        style={{ width: "100%", padding: 8, marginBottom: 16 }}
      />
      <div>
        {filtered.length === 0 && <p>Nenhum artigo encontrado.</p>}
        {filtered.map((a) => (
          <div key={a.number} style={{ marginBottom: 24, padding: 12, border: "1px solid #eee", borderRadius: 8 }}>
            <strong>Art. {a.number}</strong>
            <div style={{ marginTop: 8 }}>{a.text}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
